mapper.select:
select it_Name from item_Type
where 1=1
and it_Gender=#{gender}

select * from itemview
where 1=1
and it_gender = #{gender}

select * from itemview
where 1=1
and it_gender = #{itGender}
and s_age = #{sAge}

select * from itemview
where 1=1
and it_gender = #{itGender}
and it_name= #{itName}

select * from itemview
where 1=1
and it_gender = #{itGender}
and it_name= #{itName}
order by i_click desc

select * from itemview
where 1=1
and it_gender = #{itGender}
and it_name= #{itName}
order by i_price desc

select * from itemview
where 1=1
and it_gender = #{itGender}
and it_name= #{itName}
order by i_price asc
