mapper.select:
select * from shopView
where 1=1
and st_gender=#{stGender}

	and st_name = #{stName}


 	and s_name like upper('%' || #{searchKeyword} ||  '%')

order by s_no desc

select * from shopView where 1=1 and st_gender=#{stGender}
and to_char(s_regdate+1, 'yyyymmdd') > to_char(sysdate,'yyyymmdd')
order by s_no desc

 select * from shopnoView
where 1=1

and st_gender=#{stGender}


	and st_name = #{stName}


 	and s_name like upper('%' || #{searchKeyword} ||  '%')

order by s_no desc

mapper.update:
update shop
set s_payyn='Y'
where s_domain=#{shop}
